<?phpheader("Content-Type: application/json");require_once 'db.php';//Get the all response from$response = array();//Get Id//What Can be Updatedif(isset($_POST['submit'])){		//	Move In Updated	$title = $_POST['title'];	$storyline = $_POST['storyline'];	$language = $_POST['language'];	$genre = $_POST['genre'];	$release_date = $_POST['release_date'];	$box_office = $_POST['box_office'];	$run_time = $_POST['run_time'];	$stars = $_POST['stars'];		$statment = $connection->prepare("INSERT INTO movies (title, storyline, language, genre, release_date, box_office, run_time,stars)VALUES ('$title', '$storyline', '$language','$genre', '$release_date', '$box_office', '$run_time','$stars')");	if ($statment->execute()){		//	SUCCESS		$response['error'] =false;		$response['message'] = "Movie was successfully Inserted";	}else{		//	Failed		$response['error'] =false;		$response['message'] = "Movie Failed To Inserted";	}}else{//	We dont have any updated	$response['error'] =true;	$response['message'] = "Please Provide Us With Id";}echo json_encode($response);/*1. End Point -> /create_movie.php2. Request Type : $_POST3. id, storyline, box_office, stars */