<?phpheader("Content-Type: application/json");require_once 'db.php';//Get the all response from$response = array();$statments = $connection->prepare("SELECT * FROM movies");$results = $statments->get_result();//The array stored in all results$movies = array();if ($statments->execute()){	//	Get the all results form database	$results =$statments->get_result();	//	loop and get single row from	while ($row = $results->fetch_array(MYSQLI_ASSOC)){	$movies[] = $row;	}	$response['error'] = false; // this is no error	$response['movies'] = $movies;	$response['messages'] = "Movies returned Successfully";	$statments->close();	}else{	$response['error'] = true;	$response['messages'] = "Could Not Execute Query";}//Display Result Detailsecho json_encode($response);