<?phpheader("Content-Type: application/json");require_once 'db.php';//Get the all response from$response = array();//Get Id//What Can be Updatedif(isset($_POST['id']) && isset($_POST['storyline']) && isset($_POST['stars']) && isset($_POST['box_office'])){	//	Move In Updated	$id = $_POST['id'];	$storyline = $_POST['storyline'];	$stars = $_POST['stars'];	$box_office = $_POST['box_office'];	$statment = $connection->prepare("UPDATE movies SET storyline = '$storyline', stars = '$stars', box_office = '$box_office' WHERE id = '$id'");	if ($statment->execute()){		//	SUCCESS		$response['error'] =false;		$response['message'] = "Movie was successfully Updated";	}else{		//	Failed		$response['error'] =false;		$response['message'] = "Movie Failed To Update";	}	}else{//	We dont have any updated	$response['error'] =true;	$response['message'] = "Please Provide Us With Id";}echo json_encode($response);/*1. End Point -> /Update_movie.php2. Request Type : $_POST3. id, storyline, box_office, stars */